import { PhoneData } from '@dynamic-labs/types';
export type EmailProviders = 'magicLink' | 'dynamic';
export type ConnectWithOtpOptions = {
    provider?: EmailProviders;
};
export type IConnectWithOtpContext = {
    connectWithEmail: (email: string, options?: ConnectWithOtpOptions) => Promise<void>;
    connectWithSms: (phone: PhoneData) => Promise<void>;
    verifyOneTimePassword: (oneTimePassword: string) => Promise<void>;
    retryOneTimePassword: () => Promise<void>;
};
export type ProviderVerificationHandler<T> = {
    type: 'email' | 'sms';
    verify: (oneTimePassword: string) => Promise<{
        destination: T;
        verified: boolean;
    }>;
    retry?: () => Promise<void>;
};
export type ProviderHandler<T> = (destination: T) => Promise<ProviderVerificationHandler<T>>;
